function adjsutNavigationHeight(){window.scrollY>=100?(navWrapper.style.height="50px",navLogo.style.width="35px"):window.scrollY<100&&(navWrapper.style.height="100px",navLogo.style.width="50px")}const navWrapper=document.getElementById("nav__wrapper"),navLogo=document.getElementsByClassName("nav__logo")[0];document.addEventListener("DOMContentLoaded",()=>{adjsutNavigationHeight()}),window.addEventListener("scroll",()=>{adjsutNavigationHeight()});